using TMPro;using UnityEngine;using UnityEngine.EventSystems;public class DishEntry : MonoBehaviour, IPointerClickHandler {    TextMeshProUGUI textComponent;    Dish dishCache;    DishView dishView;        /// <summary>    /// Caches the passed dish view and dish, then caches the text component    /// found in one of its child objects and then sets its text to be    /// the name of the passed dish    /// </summary>    /// <param name="dishView"></param>    /// <param name="dish"></param>    public void Initialize(DishView dishView, Dish dish) {        this.dishView = dishView;        dishCache = dish;                textComponent = GetComponentInChildren<TextMeshProUGUI>();        textComponent.text = dishCache.GetName();    }        public void OnPointerClick(PointerEventData eventData) {        dishView.ShowRatingPanelFor(dishCache);    }}